mapper.select:
SELECT COUNT(*)
FROM customer
WHERE id=#{id} AND password=#{password}

mapper.update:

UPDATE customer 
SET access_date = sysdate, login_count=0
WHERE id=#{id}

mapper.select:

SELECT salt
FROM customer
WHERE id=#{id}

mapper.update:
UPDATE customer 
SET login_lock=#{login_lock}, login_count=login_count+1
WHERE id=#{id}

mapper.select:

SELECT login_count, login_lock
FROM customer
WHERE id=#{id}
